//将“字符串”转换为“数字”
function stringToFloat(strPara){
	var strValue,decRtn;
	//对“传入参数”中的“非数字型特殊字符”进行处理
	strValue = String(strPara).replace(/,/g,"");
	decRtn = (isNaN(parseFloat(strValue)) ? 0 : parseFloat(strValue));
	return decRtn;
}

//将“字符串”转换为“整数字”
function stringToInt(strPara){
	var strValue;
	//对“传入参数”中的“非数字型特殊字符”进行处理
	strValue = String(strPara).replace(/,/g,"");
	var intRtn = (isNaN(parseInt(strValue)) ? 0 : parseInt(strValue));
	return intRtn;
}

/**
 * 把数字截取到精确度数位上(支持到万位)
 * @param {Object} a_num 需要截取的数据
 * @param {Object} a_str - 1(个位)、10(十位)、100(百位)、1000(千位)、10000(万位)
 */
function Truncate(a_num,a_str){
	a_str = String(a_str);
	if(a_str=="") return a_num;
	switch(a_str){
		case "1":
			return Math.floor(a_num);
			break;
		case "10":
			return Math.floor(a_num / 10) * 10;
			break;
		case "100":
			return Math.floor(a_num / 100) * 100;
			break;
		case "1000":
			return Math.floor(a_num / 1000) * 1000;
			break;
		case "10000":
			return Math.floor(a_num / 10000) * 10000;
			break;
		default :
			return a_num;
	}
}

// 根据保留位进行四舍五入
function Round(dec,decimals){
	if(isNaN(dec)) return 0;
	var step;
	var temp;
	dec = Math.round(dec * 100000000) / 100000000;
	if(dec == 0){
		return dec;
	}else{
		temp = Math.abs(dec);
		if(decimals == 0 || decimals == undefined){
			temp = Math.round(temp);	
		}else if(decimals>0){
			step = Math.pow(10,decimals);
			temp = Math.round(temp * step) / step;	
		}
		
		if(dec>0) {dec = temp;}
		else {dec = -temp;}
	}
	return dec;
}

/**
 * 代替原始的jqruy的ajax方法加入遮罩功能
 * 超时设置：如果没有设置超时：默认为10超时响应
 * obj 保留所有ajax中的设置
 * @param obj
 */
function Ajax(obj){
	$.ajax({
		type	: obj.type||"post",
		url 	: obj.url,
		data 	: obj.data,
		dataType: obj.dataType||"",
		timeout	: obj.timeout || "10000",
		beforeSend:function(){
			mini.mask({
	            el: document.body,
	            cls: 'mini-mask-loading',
	            html: obj.loadText||'加载中...'
	        });
		},
		complete:function(){
			mini.unmask(document.body);
		},
		success :function(msg){
			try{
				obj.success(msg);
			}catch(e){
				showTips(e.message);
			}
		},
		error :function(jqXHR, textStatus, errorThrown){
			if(textStatus=="timeout"){
				showTips("请求超时，请重试！如仍有问题请检查本地网络或与系统管理员联系！");
			}else{
				showTips("系统繁忙，请稍后再试！");
			}
		}
	});
};

/**
 * 空字符串转换
 * @param obj
 */
function empty(obj){
	if(obj == null || obj == "null" || typeof(obj)=="undefined" || $.trim(obj)==""){
		return "";
	}
	return obj;
}

/**
 * miniForm数据包装
 * @param {Object} formId - 表单id
 */
function serializeArray(formId){
	var form = new mini.Form(formId);
	var data = [];
	var fields = form.getFields();
	for (var i = 0; i < fields.length; i++) {
		var field = fields[i];
		var name = field.getName();
		if(empty(name) == "")continue;
		var value = field.getValue();
		if($(field.getEl()).hasClass("mini-currency")){
			value =  reThousandFormat(value);
		}else if(field.type == "datepicker"){
			var format = field.format || "yyyy-MM-dd";
			value = mini.formatDate (value, format);
		}else if(field.type == "monthpicker"){
			var format = field.format || "yyyy-MM";
			value = mini.formatDate (value, format);
		}
		data.push({name: name, value: empty(value)});
	}
	return data;
}

function arrayToObject(data){
	var o={};
    $.each(data, function () {
        if (o[this.name]) {
            if (!o[this.name].push) {
                o[this.name] = [o[this.name]];
            }
            o[this.name].push(this.value || '');
        } else {
            o[this.name] = this.value || '';
        }
    });
    return o;
}

/**
 * 普通Form数据包装(form表单包含非mini控件且控件name不允许重复)
 * @param {Object} formId - 表单id
 */
function serializeArrayNormal(formId){
	var form = new mini.Form(formId);
	var data = $("#"+formId+" :input[name]").serializeArray();
	var fields = form.getFields();
	for (var i = 0; i < fields.length; i++) {
		var field = fields[i];
		var name = field.getName();
		var value = field.getValue();
		if($(field.getEl()).hasClass("mini-currency")){
			for(var j = 0; j < data.length; j++){
				if(data[j].name == name){
					data[j].value = reThousandFormat(empty(value));
				}
			}
		}
	}
	return data;
}

/**
 * 设置表单显示模式
 * @param {Object} formId - 表单id
 * @param {Object} viewMode - (true|false)是否查看模式
 */
function showViewModel(formId, viewMode) {
	var form = new mini.Form(formId);
    var fields = form.getFields(); 
    if(viewMode){
    	for (var i = 0; i < fields.length;i++) {
    		var field = fields[i];
    		if(field.type == "hidden") continue;
    		var el = field.getEl();
    		var value =  field.getText ? field.getText() : field.getValue();
    		if(field.type == "radiobuttonlist" && value){
    			value = field.getItemText(field.getItem(value));
    		}
    		if(value){
    			value = value.replace(/\n/g, "<br>");
			}
    		var label = "<div class='_label _label_"+ field.name +"' style='text-align:inherit; white-space:normal;width:100%;padding-left:2px'>" + value + "</div>";
    		$(el).siblings("._label").remove();
    		$(el).hide().before(label);
        }
		$("#"+formId).find("a:not(.fixed)").hide();
		$(".mini-pager .mini-button").show();//分页组件需显示
		$("#"+formId).find(".uploadify").hide();//上传组件
    }else{
    	for (var i = 0; i < fields.length;i++) {
    		var field = fields[i];
    		if(field.type == "hidden") continue;
    		var el = field.getEl();
    		$(el).siblings("._label").remove();
    		$(el).show();
        }
		$("#"+formId).find("a:not(.fixed)").show();
		$("#"+formId).find(".uploadify").show();//上传组件
    }
}

/**
 * @param {Object} formId
 */
function disableForm(formId){
	var form = new mini.Form(formId);
	var fields = form.getFields();                
    for (var i = 0, l = fields.length; i < l; i++) {
        var c = fields[i];
        if (c.setReadOnly) c.setReadOnly(true);     //只读
        if (c.setIsValid) c.setIsValid(true);      //去除错误提示
        if (c.addCls) c.addCls("asLabel");          //增加asLabel外观
        c.setEnabled(false);
    }
	//按钮不可见
	$("#"+formId).find("a:not(.fixed)").hide();
	//上传组件隐藏
	$("#"+formId).find(".uploadify").hide();
}

/**
 * 添加千位符，并保留precision位小数(默认为2位)
 * s字符型金额
 */
function thousandFormat(s,precision){
	if(isNaN(precision)) precision = 2;
	s = reThousandFormat(s);
	if(empty(s) == "") return new Number(0).toFixed(precision);
	 if(isNaN(s)){
    	alert("无效数字");
    	return new Number(0).toFixed(precision);  
    }
	//正式处理
	var sign = (s < 0) ? '-' : '';
	var i = parseInt(s = Math.abs(s).toFixed(precision)) + "";
	var j = (j = i.length) > 3 ? j % 3 : 0;
	return sign + (j ? i.substr(0, j) + "," : "") + i.substr(j).replace(/(\d{3})(?=\d)/g, "$1,") + (precision ? "." + Math.abs(s - i).toFixed(precision).slice(2) : "");
} 

/**
 * 还原千位符的数字
 * s字符型金额
 */
function reThousandFormat(s){
	if(empty(s)=="") return "";
	return new String(s).replace(/,/g, "");
} 

function decodeToJson(jsonSting){
	try{
		return mini.decode(jsonSting.replace(/\r/g,'\\r').replace(/\n/g,'\\n').replace(/\t/g,'\\t'));
	}catch(e){
		showTips("数据加载错误，请与管理员联系！");
		return "";
	}
}

/**
 * 填充表单数据
 * @param {Object} formId - 表单id
 * @param {Object} data - 填充数据Json数据
 * @param {Object} notDefaultFill - 如果填充数据为空是否进行默认填充,默认为默认填充
 */
function setFormData(formId,data,notDefaultFill){
	var form = new mini.Form(formId);
	form.setData(data);
	var fields = form.getFields();
	for (var i = 0; i < fields.length; i++) {
		var field = fields[i];
		var value = field.getValue();
		if($(field.getEl()).hasClass("mini-currency")){
			field.setValue(thousandFormat(value||"0"));
		}else if(field.type == "radiobuttonlist" && !notDefaultFill){
			field.setValue(value||field.defaultValue);
		}else if(!value && field.defaultValue && !notDefaultFill){
			field.setValue(value||field.defaultValue);
			if (field.type == "buttonedit") {
				field.setText(field.defaultText);
			}
		}
	}
}

/**
 * 部门、岗位、用户通用选择
 * @param {Object} control_name - mini-buttonedit控件,用于设置no和name
 * @param {Object} type_el_name - mini-hidden控件,用于设置对象类型
 * @param {Object} callback - 回调函数
 * @param {Object} flag - 选择类型标志(默认值111) : 111-部门岗位人员, 100-部门, 010-岗位, 011-岗位人员， 001 人员
 * @param {Object} multiSelect - 是否多选(默认值true)
 */
function selectAddress(control_name, type_el_name, flag, multiSelect, callback){
	var btnEdit = (typeof(control_name)==="object")? control_name : mini.getbyName(control_name);
	var type_el = type_el_name ? mini.getbyName(type_el_name) : null;
	var callback = callback || function(){};
	var flag = flag || "111";
	var multiSelect = (multiSelect === false)? false : true;
	mini.open({
        url: "addressbook?multiSelect="+ multiSelect +"&flag="+ flag,
        title: "地址本",
        width: 800,
        height: 600,
		onload:function(){
			var iframe = this.getIFrameEl();
			if(type_el){
				iframe.contentWindow.SetData({object_ids:btnEdit.getValue(), object_names:btnEdit.getText(), object_types:type_el.getValue()});
			}else{
				iframe.contentWindow.SetData({object_ids:btnEdit.getValue(), object_names:btnEdit.getText()});
			}
		},
        ondestroy: function (action) {
			if (action == "ok") {
                var iframe = this.getIFrameEl();
                var data = iframe.contentWindow.GetData();
                data = mini.clone(data);    //必须
                if (data) {
                    var ids = [], names = [], types = [];
					for(var i = 0; i < data.length; i++){
						ids.push(data[i].object_id);
						names.push(data[i].object_name);
						if(type_el){
							types.push(data[i].object_type);
						}
					}
					btnEdit.setValue(ids.join(","));
					btnEdit.setText(names.join(","));
					if(type_el){
						type_el.setValue(types.join(","));
					}
					//回调函数
					if(multiSelect){
						callback(data);
					}else{
						callback(data[0]);
					}
                }
            }
        }
    });
}

