/**
 * 计算税率
 * amt1:含税金额
 * amt2：不含税金额
 */
function getTaxRate(amt1,amt2){
	var num1= new Number(reThousandFormat(amt1)).toFixed(2);
	var num2= new Number(reThousandFormat(amt2)).toFixed(2);
	if(num2==0){
		return 0;
	}
	return toNum((((num1-num2)/num2)*100).toFixed(2));
} 

/**
 * 计算税额
 * amt1:含税金额
 * rate：税率
 */
function getTaxAmountByRate(amt1,rate){
	var num1= toNum(reThousandFormat(amt1));
	var num2= toNum(reThousandFormat(rate));
	return toNum(num1-toNum((num1*100/(num2+100)).toFixed(2)));
}

/**
 * 计算税额
 * amt1:含税金额
 * amt2：不含税金额
 */
function getTaxAmountByNotax(amt1,amt2){
	var num1= toNum(reThousandFormat(amt1));
	var num2= toNum(reThousandFormat(amt2));
	return toNum((num1-num2).toFixed(2));
}

/**
 * 计算不含税金额
 * amt1:含税金额
 * rate：税率
 */
function getNoTaxAmount(amt1,rate){
	var num1= toNum(reThousandFormat(amt1));
	var num2= toNum(reThousandFormat(rate));
	return toNum((num1*100/(num2+100)).toFixed(2));
}

/**
 * 计算含税金额
 * amt1:不含税金额
 * rate：税率
 */
function getAmount(amt1,rate){
	var num1 = new Number(reThousandFormat(amt1)).toFixed(2); 
	var num2= new Number(reThousandFormat(rate)).toFixed(2);
	return toNum(((num1/(100-num2))*100).toFixed(2));
}

/**
 * 字符串转数字
 * @author kevin
 * 
 * @param value
 * @returns {Number}
 */
function toNum(value){
	return new Number(new Number(value).toFixed(2));
}

/**
 * 时间戳转成时间字符串（yyyy-MM-dd HH:mm）
 * @author kevin
 * 
 * @param timestamp
 * @returns
 */
function onTimestampToDateStr(timestamp) {
	var d = new Date(timestamp);
	var month = d.getMonth() + 1;
	var day = d.getDate();
	var hour = d.getHours();
	var minute = d.getMinutes();
	if (month >= 1 && month <= 9) {
		month = "0" + month;
	}
	if (day >= 0 && day <= 9) {
		day = "0" + day;
	}
	if (hour >= 0 && hour <= 9) {
		hour = "0" + hour;
	}
	if (minute >= 0 && minute <= 9) {
		minute = "0" + minute;
	}
	return (d.getFullYear()) + "-" + month + "-" + day + " " + hour + ":" + minute;
}

